CREATE DATABASE Ldw_BCC
      COLLATE Latin1_General_100_BIN2_UTF8;

CREATE EXTERNAL DATA SOURCE ecdc_cases WITH (
    LOCATION = 'https://pandemicdatalake.blob.core.windows.net/public/curated/covid-19/ecdc_cases/'
);

CREATE MASTER KEY ENCRYPTION BY PASSWORD = 'Password123';

CREATE DATABASE SCOPED CREDENTIAL WorkspaceIdentity
WITH IDENTITY = 'Managed Identity';
GO
CREATE EXTERNAL DATA SOURCE bcc_trips WITH (
    LOCATION = 'https://demohc7on2ywpzovapoc.dfs.core.windows.net/',
    CREDENTIAL = WorkspaceIdentity
);

CREATE EXTERNAL FILE FORMAT ParquetFormat WITH (  FORMAT_TYPE = PARQUET );
GO
CREATE EXTERNAL FILE FORMAT CsvFormat WITH (  FORMAT_TYPE = DELIMITEDTEXT );

select top 10  *
from openrowset(bulk '/enriched/BCC/TripData/*.parquet',
                data_source = 'bcc_trips',
                format='parquet') as a

create schema ecdc_adls;

create external table ecdc_adls.cases (
    date_rep                   date,
    day                        smallint,
    month                      smallint,
    year                       smallint,
    cases                      smallint,
    deaths                     smallint,
    countries_and_territories  varchar(256),
    geo_id                     varchar(60),
    country_territory_code     varchar(16),
    pop_data_2018              int,
    continent_exp              varchar(32),
    load_date                  datetime2(7),
    iso_country                varchar(16)
) with (
    data_source= ecdc_cases,
    location = 'latest/ecdc_cases.parquet',
    file_format = ParquetFormat
);

select * from ecdc_adls.cases
